    return (
        <div className = "shoeDetail">
            <br></br> <br></br> <br></br>
            <h3>{selectedShoe.name}</h3>

            <a href = "/products">Go back.....</a>

            <button className = "basketB"> Click to Buy: Â£{selectedShoe.price}</button>
            {/* <br></br> */}

                <div  className = "shoeDetailImg">
