import React , { useState } from 'react';
import Button from 'react-bootstrap/Button';
import ProductDetail from '../components/ProductDetail.js';

const MensList = ({mens}) => {
  const [selectedMens, setSelectedMen] = useState(null);

  console.log({mens})

    const mensItems = mens.map((men, index) => {
      return <Button variant="light" onClick = {() => onItemsClick(men) }>{men.name}</Button>
      console.log({men})
    })


        const onItemsClick = function(men){
          setSelectedMen(men);
    }

    const onBackButton = function () {
      setSelectedMen(null);
    }


  if (selectedMens) {
    return (

      <div>
      <ul>
        <ProductDetail genderMen={selectedMens}/>
        <button Class = "button_2" onClick={() => onBackButton()}>Choose another Region</button>
      </ul>
    </div>
    );

  } else if (!selectedMens) {
    return (      
    <div>
    <ul>
      {mensItems}
    </ul>
  </div>

    )}
};

export default MensList;
